import React, { useState, useCallback } from 'react'
import { SolverContainer, Title, Grid, Cell, SolveBtn } from './SolverElements'

const Solver = () => {
    const [grid, setGrid] = useState([
        [0, 0, 0, 0, 0, 0, 6, 8, 0],
        [0, 0, 0, 0, 7, 3, 0, 0, 9],
        [3, 0, 9, 0, 0, 0, 0, 4, 5],
        [4, 9, 0, 0, 0, 0, 0, 0, 0],
        [8, 0, 3, 0, 5, 0, 9, 0, 2],
        [0, 0, 0, 0, 0, 0, 0, 3, 6],
        [9, 6, 0, 0, 0, 0, 3, 0, 8],
        [7, 0, 0, 6, 8, 0, 0, 0, 0],
        [0, 2, 8, 0, 0, 0, 0, 0, 0]
    ])

    // useEffect(()=> {
    //     fetch(`http://127.0.0.1:5000/getGrid`)
    //     .then(response => response.json())
    //     .then(data => setGrid(data))
    // }, [])

    const solve = () => {
        fetch(`http://127.0.0.1:5000/solve`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({grid: grid})
        }).then(response => {
            if(response.ok){
                return response.json()
            }
        }).then(data => setGrid(data))
    }

    const handleCellChange = useCallback(
        (r,c) => (e) => {
            console.log(e.target.value)
            var newgrid = grid;
            newgrid[r][c] = parseInt(e.target.value)
            setGrid(newgrid);
            console.log(grid)
        }, [grid]
    )

    return (
        <SolverContainer>
            <Title>Sudoku Solver</Title>
            <Grid>
                {grid.map((row, r) => (
                    row.map((num, c) => (
                        <Cell key={(r*grid.length)+c} value={grid[r][c]} onChange={() => handleCellChange(r,c)}/>
                    ))
                ))}
            </Grid>
            <SolveBtn onClick={solve}>Solve</SolveBtn>
        </SolverContainer>
    )
}

export default Solver
